import React, { useContext} from "react";
import { orderContext } from "./components/CartContext";
import ItemCartSkeleton from "./components/ItemCartSkeleton";
import {Link} from 'react-router-dom'

import ItemCart from './components/ItemCart';

function ItemCartList() {
  
        const order = useContext(orderContext);
      
      return (
        <div className="col-12 col-md-8 item-list-products-column">
            {order.length >=1 ?<div className="carrito">
            {order.map((item,i) => {
              return (
                <ItemCartSkeleton
                  key={i}
                  index= {i}
                  image={item.image}
                  id={item.id}
                  nombre={item.nombre}
                  precio={item.precio}
                  cantidad={item.cantidad}
                  total= {item.precio * item.cantidad}
                />
              );
            })}
          </div>: 
          <div>
            <h1>El carro se encuentra vac√≠o!</h1>
            <li className="nav__links">
            <Link to="/Products">ir a productos</Link>
            </li>
          </div>
          }
          </div>
        );
      }
      
      
      export default ItemCartList;
