import React, { useState, useContext, useEffect } from "react";
import {Link} from "react-router-dom";
import { orderContext } from "./CartContext";
import ItemCartSkeletonTotal from "./ItemCartSkeletonTotal";
import ItemCartSkeleton from "./ItemCartSkeleton";


function PurchaseFinalized() {
    const order = useContext(orderContext);
    return (<>

        <h1>Muchas gracias por tu compra</h1>
        <h5>A continuaci√≥n, te dejamos el detalle de los productos que compraste:</h5>
        {order.map((item,i) => {
                return (
                  <ItemCartSkeleton
                    key={i}
                    index= {i}
                    image={item.image}
                    id={item.id}
                    nombre={item.nombre}
                    precio={item.precio}
                    cantidad={item.cantidad}
                    total= {item.precio * item.cantidad}
                  />
  
                );
              })}
            
            </>
  
          );
}

export default PurchaseFinalized
