        function total(){
          let total = 0
          for(let num in desing.filed.wiring){
           for( let order of desing.filed.wiring[num].inverter ){
             total +=order.precio;
           }
          }
          elTotal.value = total
        }
      
      return (<>
      {order.map((item,i) => {
              return (
                <>
                <ItemCartSkeleton
                  key={i}
                  index= {i}
                  image={item.image}
                  id={item.id}
                  nombre={item.nombre}
                  precio={item.precio}
                  cantidad={item.cantidad}
                  total= {item.precio * item.cantidad}
                />
                <p>TOTAL {order.price}</p>
                </>
              );
            })}
          </>

        );
      }
      
      
      export default ItemCartList;
