import React, { useContext } from "react";
import {  orderUpdateContext } from "../components/CartContext";

function ItemCart(props) {
  //const order = useContext(orderContext);
  const {deleteItem}= useContext(orderUpdateContext)



  return (
    <div className="itemCart" id={props.id}>
      <img src={props.image} alt="imageCarro"></img>
      <p>{props.nombre}</p>
      <p>${props.precio} x unidad</p>
      <p>total: $ {props.total}</p>
      <p>cantidad: {props.cantidad}</p>
      <button onClick={()=>deleteItem(props.index)}>borrar</button>




      <div id={props.id} class="card">
      <img class="card-img-top col-4" src={props.image} alt="Imagen articulo carrito"></img>
      <div class="card-body col.8">
        <Link to={props.id}><h5 class="card-title">{props.name}</h5></Link>
        <h4 class="card-text">${props.precio}</h4>
        <p class="card-text-small">Total: </p>
        <p class="card-text-small">Envio Gratis</p>
      </div>
    </div>
    </div>
  );
}

export default ItemCart;
