import React, { useContext} from "react";
import { orderContext } from "./CartContext";
import ItemCartSkeleton from "./ItemCartSkeleton";
import {Link} from 'react-router-dom'


function ItemCartList() {
  
        const order = useContext(orderContext);

        function checkInventario(inventario, item) {
          for (i = 0; i < inventario.length; i++) {
              if (inventario[i].nombre === item) {
                return inventario[i].cantidad;
              } else {
                return 0;
              }
            }
          }
      
      return (<>
      {order.map((item,i) => {
              return (
                <ItemCartSkeleton
                  key={i}
                  index= {i}
                  image={item.image}
                  id={item.id}
                  nombre={item.nombre}
                  precio={item.precio}
                  cantidad={item.cantidad}
                  total= {item.precio * item.cantidad}
                />
              );
            })}
          </>

        );
      }
      
      
      export default ItemCartList;
